(12, 1, '2023-03-28 01:30:42', '2023-03-28 01:30:42', '<!DOCTYPE html>\r\n<html lang=\"es\">\r\n\r\n<head>\r\n    <meta charset=\"UTF-8\">\r\n    <meta http-equiv=\"X-UA-Compatible\" content=\"IE=edge\">\r\n    <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\">\r\n    <title>Currículo de Sandra</title>\r\n    <meta name=\"author\" content=\"Sandra Madarnas\">\r\n\r\n    <link rel=\"stylesheet\" href=\"style.css\">\r\n    <script src=\"index.js\"></script>\r\n\r\n    <link rel=\"stylesheet\" href=\"https://use.fontawesome.com/releases/v5.8.1/css/all.css\"\r\n        integrity=\"sha384-50oBUHEmvpQ+1lW4y57PTFmhCaXp0ML5d60M1M7uH2+nqUivzIebhndOJK28anvf\" crossorigin=\"anonymous\">\r\n    <link href=\"https://cdn.jsdelivr.net/npm/bootstrap@5.3.0-alpha1/dist/css/bootstrap.min.css\" rel=\"stylesheet\"\r\n        integrity=\"sha384-GLhlTQ8iRABdZLl6O3oVMWSktQOp6b7In1Zl3/Jr59b6EGGoI1aFkw7cmDA6j6gD\" crossorigin=\"anonymous\">\r\n</head>\r\n\r\n<body>\r\n    <!-- NavBar -->\r\n    <nav class=\"navbar navbar-expand-lg bg-light fixed-top navbar-custom\">\r\n        <div class=\"container\">\r\n            <a class=\"navbar-brand\" href=\"#inicio\"><strong>SANDRA MADARNAS</strong></a>\r\n            <button class=\"navbar-toggler\" type=\"button\" data-bs-toggle=\"collapse\"\r\n                data-bs-target=\"#navbarSupportedContent\" aria-controls=\"navbarSupportedContent\" aria-expanded=\"false\"\r\n                aria-label=\"Toggle navigation\">\r\n                <span class=\"navbar-toggler-icon\"></span>\r\n            </button>\r\n            <div class=\"collapse navbar-collapse\" id=\"navbarSupportedContent\">\r\n                <ul class=\"navbar-nav me-auto mb-2 mb-lg-0\">\r\n                    <li class=\"nav-item\">\r\n                        <a class=\"nav-link\" aria-current=\"page\" href=\"#acerca-de-mi\">ACERCA DE MI</a>\r\n                    </li>\r\n                    <li class=\"nav-item\">\r\n                        <a class=\"nav-link\" aria-current=\"page\" href=\"#servicios\">SERVICIOS</a>\r\n                    </li>\r\n                    <li class=\"nav-item\">\r\n                        <a class=\"nav-link\" href=\"#formacion\">FORMACIÓN</a>\r\n                    </li>\r\n                    <li class=\"nav-item\">\r\n                        <a class=\"nav-link\" href=\"#experiencia\">EXPERIENCIA</a>\r\n                    </li>\r\n                    <li class=\"nav-item\">\r\n                        <a class=\"nav-link\" href=\"#portafolio\">PORTAFOLIO</a>\r\n                    </li>\r\n                    <li class=\"nav-item\">\r\n                        <a class=\"nav-link\" href=\"#contacto\">CONTACTO</a>\r\n                    </li>\r\n                </ul>\r\n            </div>\r\n        </div>\r\n    </nav>\r\n    <!-- Fin NavBar -->\r\n\r\n\r\n    <!-- Portada - Header -->\r\n    <header class=\"d-flex align-items-center custom-header\" id=\"inicio\">\r\n        <div class=\"container text-white-custom text-shadow text-center\">\r\n            <h1 class=\"mb-4\">Sandra Madarnas<br>\r\n                <small class=\"text-primary-custom\">Ingeniero de Sistemas | Programadora Web | Desarrolladora\r\n                    Frontend</small>\r\n            </h1>\r\n            <a class=\"btn btn-custom btn btn-primary btn-xl\" href=\"https://www.linkedin.com/in/sandra-madarnas/\" target=\"_blank\" rel=\"noopener\">\r\n                LinkedIn\r\n            </a>\r\n            <a class=\"btn btn-custom btn btn-primary btn-xl\" href=\"https://github.com/SandraMadarnas\" target=\"_blank\" rel=\"noopener\">\r\n                GitHub\r\n            </a>\r\n        </div>\r\n    </header>\r\n    <!-- Fin Portada - Header -->\r\n\r\n\r\n    <!-- Acerca de Mi - Datos Personales -->\r\n    <section class=\"datos-personales about\">\r\n        <div id=\"acerca-de-mi\"></div>\r\n        <div class=\"container my-4 p-4 border rounded-3\">\r\n            <div class=\"row\">\r\n               