(13, 1, '2023-03-28 01:31:02', '2023-03-28 01:31:02', '  * {\r\n      margin: 0;\r\n      padding: 0;\r\n  }\r\n\r\n  body {\r\n      background-color: azure;\r\n  }\r\n\r\n  .navbar-custom {\r\n      box-shadow: 0 0.5rem 0.8rem #00000080;\r\n  }\r\n\r\n\r\n  /* Header - Portada */\r\n  .custom-header {\r\n      background: url(\"https://cdn.pixabay.com/photo/2017/09/05/10/19/business-2717063_1280.jpg\");\r\n      background-repeat: no-repeat;\r\n      background-position: center bottom;\r\n      background-attachment: fixed;\r\n      background-size: 100%;\r\n      min-height: 100vh;\r\n      background-size: cover;\r\n      background-blend-mode: overlay;\r\n      background-color: rgba(0, 0, 0, 0.7);\r\n      color: white;\r\n  }\r\n\r\n\r\n  /* About - Acerca de Mi */\r\n  .datos-personales {\r\n      margin-top: 4rem;\r\n  }\r\n\r\n  .about {\r\n      position: relative;\r\n  }\r\n\r\n  #acerca-de-mi {\r\n      position: absolute;\r\n      top: -56px;\r\n  }\r\n\r\n\r\n  /* Skills - Servicios */\r\n  #servicios {\r\n      position: absolute;\r\n      top: -56px;\r\n  }\r\n\r\n  .skills {\r\n      background-color: black;\r\n      text-align: center;\r\n      color: white;\r\n      margin-top: 2rem;\r\n      padding: 3rem;\r\n      position: relative;\r\n  }\r\n\r\n  .logo-custom {\r\n      width: 5rem;\r\n      height: 5rem;\r\n  }\r\n\r\n  .warpper {\r\n      display: flex;\r\n      flex-direction: column;\r\n      align-items: center;\r\n  }\r\n\r\n  .tab {\r\n      cursor: pointer;\r\n      padding: 10px 20px;\r\n      margin: 0px 2px;\r\n      background: #000;\r\n      display: inline-block;\r\n      color: #fff;\r\n      border-radius: 3px 3px 0px 0px;\r\n      box-shadow: 0 0.5rem 0.8rem #00000080;\r\n  }\r\n\r\n  .panels {\r\n      background: #fffffff6;\r\n      box-shadow: 0 2rem 2rem #00000080;\r\n      min-height: 200px;\r\n      width: 100%;\r\n      margin-top: 0.3rem;\r\n      border-radius: 3px;\r\n      overflow: hidden;\r\n      padding: 20px;\r\n  }\r\n\r\n  .panel {\r\n      display: none;\r\n      animation: fadein .8s;\r\n  }\r\n\r\n  @keyframes fadein {\r\n      from {\r\n          opacity: 0;\r\n      }\r\n\r\n      to {\r\n          opacity: 1;\r\n      }\r\n  }\r\n\r\n  .panel-title {\r\n      font-size: 1.5em;\r\n      font-weight: bold\r\n  }\r\n\r\n  .radio {\r\n      display: none;\r\n  }\r\n\r\n  #one:checked~.panels #one-panel,\r\n  #two:checked~.panels #two-panel,\r\n  #three:checked~.panels #three-panel,\r\n  #four:checked~.panels #four-panel,\r\n  #five:checked~.panels #five-panel {\r\n      display: block\r\n  }\r\n\r\n  #one:checked~.tabs #one-tab,\r\n  #two:checked~.tabs #two-tab,\r\n  #three:checked~.tabs #three-tab,\r\n  #four:checked~.tabs #four-tab,\r\n  #five:checked~.tabs #five-tab {\r\n      background: #fffffff6;\r\n      color: #000;\r\n      border-top: 3px solid #000;\r\n  }\r\n\r\n\r\n  /* Servicios */\r\n  #servicios {\r\n      border-top: 10px solid #0d6efd;\r\n      margin-top: 1rem;\r\n  }\r\n\r\n  .servicios {\r\n      border-top: 5px solid #0d6efd;\r\n      border-bottom: 5px solid #0d6efd;\r\n      border-left: 10px solid #0d6efd;\r\n      border-right: 10px solid #0d6efd;\r\n      padding: 3%;\r\n      width: inherit;\r\n  }\r\n\r\n  .button-custom {\r\n      margin: 0.5rem;\r\n  }\r\n\r\n\r\n  /* Formación - Experiencia */\r\n  .education {\r\n      position: relative;\r\n  }\r\n\r\n  #formacion {\r\n      position: absolute;\r\n      top: -56px;\r\n  }\r\n\r\n  .experience {\r\n      position: relative;\r\n  }\r\n\r\n  #experiencia {\r\n      position: absolute;\r\n      top: -56px;\r\n  }\r\n\r\n\r\n  /* Mis Logros - Portafolio */\r\n  .portfolio {\r\n      background: url(\"https://www.edukonexion.com/wp-content/uploads/elementor/thumbs/dummy016-vintage-oy5pgalks9an16x91gq03kcf7cbxb6zmkv5n2k04mo.jpg\");\r\n      background-repeat: no-repeat;\r\n      background-position: center bottom;\r\n      background-attachment: fixed;\r\n      background-size: 100%;\r\n      min-height: 100vh;\r\n      background-size: cover;\r\n      background-blend-mode: overlay;\r\n      background-color: rgba(0, 0, 0, 0.7);\r\n      color: white;\r\n      position: relative;\r\n  }\r\n\r\n  #portafolio {\r\n      position: absolute;\r\n      top: -56px;\r\n  }\r\n\r\n  .custom-portfolio a {\r\n      text-decoration: none;\r\n  }\r\n\r\n\r\n  /* Contacto */\r\n  .contact {\r\n      position: relative;\r\n  }\r\n\r\n  #contacto {\r\n      position: absolute;\r\n      top: -56px;\r\n  }', '', '', 'publish', 'closed', 'closed', '', '13', '', '', '2023-03-28 01:31:02', '2023-03-28 01:31:02', '', 0, 'http://localhost/sandra-madarnas/?post_type=custom-css-js&#038;p=13', 0, 'custom-css-js', '', 0),
